package de.gamingcraft.overlay;import de.gamingcraft.UtilityClient;import de.gamingcraft.overlay.modules.*;import net.minecraft.client.gui.FontRenderer;import net.minecraft.client.gui.Gui;import net.minecraft.client.renderer.GlStateManager;import net.minecraft.util.EnumChatFormatting;public class ModuleHandler {    private final static IModule[] modules = new IModule[] {            new FPSModule(),            new CPSModule(),            new CoordsModule(),            new ClockModule(),            new DateModule()    };    private final static int op_x = 4, op_y = 4, mod_height = 9;    public static void loop(FontRenderer fr) {        int largest = 0;        int x = 1;        for (IModule mod : modules) {            String str = UtilityClient.CURRENT_THEME.getPrefix() + mod.getName() + EnumChatFormatting.GRAY + ": " + UtilityClient.CURRENT_THEME.getSuffix() + mod.getValue();            int y = fr.getStringWidth(str);            if(y > largest) largest = y;            x++;        }        GlStateManager.enableAlpha();        Gui.drawRect(op_x-2, op_y-2, largest + op_x+2, x*mod_height + op_y+2, Integer.MIN_VALUE);        GlStateManager.disableAlpha();        int z = 1;        for (IModule mod : modules) {            String str = UtilityClient.CURRENT_THEME.getPrefix() + mod.getName() + EnumChatFormatting.GRAY + ": " + UtilityClient.CURRENT_THEME.getSuffix() + mod.getValue();            fr.drawStringWithShadow(str, op_x, op_y + (mod_height*z), 0);            z++;        }        fr.drawStringWithShadow(UtilityClient.CURRENT_THEME.getPrefix() + UtilityClient.getClientName() + " " + UtilityClient.getVersion(), 4, 4, 1428160512);    }}